import setColor from "./setColor";

const selectPlace = (setPlace, place, item, e) => {
  e.preventDefault();

  setColor(item.id);
  let objectLocal = { id: item.id, x: item.cords.s };

  const localObject = window.localStorage.getItem("places");
  if (localObject !== null) {
    localObject.concat({ id: item.id, x: item.cords.x });
  } else {
    window.localStorage.setItem("places");
  }

  //setPlace( [...place, {id: itemId}]);
  console.log(place);
};

export default selectPlace;
