import "./style/style.css";

const Frame = (item) => {
  const setColor = (id) => {
    const field = document.querySelector(`.${id}`);
    if (field.style.backgroundColor === "chocolate") {
      field.style.background = "white";
    } else {
      field.style.background = "chocolate";
    }
  };

  const positionY = item.cords.y * 6.5;
  const positionX = item.cords.x * 12.5;
  if (item.reserved)
    return (
      <td
        className="itemStyle"
        style={{
          left: positionY + "%",
          top: positionX + "%",
          background: "silver",
        }}
      >
        {item.id}
      </td>
    );
  return (
    <button
      className={`itemStyle ${item.id}`}
      style={{ left: positionY + "%", top: positionX + "%" }}
      onClick={(e) => myFun(item.id)}
    >
      {item.id}
    </button>
  );
};

export default Frame;
