  let objectLocal = { id: item.id, x: item.cords.x ,y:item.cords.y};
  console.log(objectLocal);

  const localObject = JSON.parse(window.localStorage.getItem("places"));
  if (localObject !== null) {
      localObject.map(i => {
          arrayPlaces.push(i)
      })
      //arrayPlaces.push(JSON.parse(localObject))
