import setColor from "./setColor";

const selectPlace = (setPlace, place, item, e) => {
  e.preventDefault();
  let newLocalObject 
  let arrayPlaces = []

  setColor(item.id);
  let objectLocal = { id: item.id, x: item.cords.x };
  console.log(objectLocal);

  const localObject = window.localStorage.getItem("places");
  if (localObject !== null) {
      localObject.map(i => {
          arrayPlaces.push(JSON.parse(i))
      })
      //arrayPlaces.push(JSON.parse(localObject))
      arrayPlaces.push(objectLocal)
    //newLocalObject = localObject.concat({objectLocal})
    //localObject.concat(objectLocal);
    window.localStorage.setItem("places",JSON.stringify(arrayPlaces))
  } else {
    arrayPlaces.push(objectLocal)
    window.localStorage.setItem("places",JSON.stringify(arrayPlaces));
  }


  const tab = []
  let obj1 = {id: 0, x:0}
  let obj2 = {id:1,x:1}
  tab.push(obj1)
  tab.push(obj2)

  window.localStorage.setItem("xd",JSON.stringify( tab))

  const testt = JSON.parse( window.localStorage.getItem("xd"))
  console.log(testt[0]);

};

export default selectPlace;
