import React, { useState } from "react";
import "./style/style.css";

const HomePage = () => {
  const [numberOfSets, setNumberOfSets] = useState("");
  const [statusCheckbox,setStatusCheckbox] = useState(false)

  const setCheckbox = (e) => {
      if(statusCheckbox) setStatusCheckbox(false)
      else setStatusCheckbox(true)
  }

  return (
    <div className="justify-content-md-center globalDiv">
      <div>
        <small className="textNumberStyle">Liczba miejsc:</small>
        <input className="col-6 inputHome" type="text"></input>
      </div>

      <div className="mt-5">
        <input className="checkboxStyle float-left" type="checkbox" onChange={e => {setCheckbox(e.target.checked)}} />
        <small className="checkboxTextStyle">
          Czy miejsca mają być obok siebie?
        </small>
      </div>

      <div className="mt-5">
        <button className="col-12 buttonHome btn btn-outline-dark" type="submit" value="Submit">
          Wybierz miejsca
        </button>
      </div>
      <p>{String(statusCheckbox)}</p>
    </div>
  );
};

export default HomePage;
