import setColor from "./setColor"

const selectPlace = async (setPlace,place,itemId) =>{
    await setColor(itemId);
    await window.localStorage.setItem("places",place)
    // setPlace(itemId)
    // setPlace([...place,itemId])
    // window.localStorage.setItem("places",place)
}

export default selectPlace
