import axios from "axios";

const headers = {
  "Content-Type": "application/json",
};

const Seats = {
  getAllSeats,
  selectPlace,
};

async function getAllSeats() {
  const response = axios.get("http://localhost:3000/seats");
  return response;
}

async function selectPlace() {
  const numberOfSets = window.localStorage.getItem("numberOfSets");
  const arraySeats = [];

  const respo = await getAllSeats();
  console.log(respo.data);
  for (let i = 0; i < numberOfSets; i++) {
    const positionY = Math.floor(Math.random() * (15 + 1));
    const positionX = Math.floor(Math.random() * (11 + 1));
    console.log("position y: " + positionY);
    console.log("position x: " + positionX);
    respo.data.map((item) => {
      if (item.cords.x == positionX && item.cords.y == positionY) {
        console.log("object x,y" + item.cords.x + " " + item.cords.y);
        arraySeats.push(item)
      }
    });

  }
}

export default Seats;
