import setColor from "./setColor"

const selectPlace = (setPlace,place,itemId) =>{
    setColor(itemId);
    setPlace([...place,itemId])
    let obj = window.localStorage.getItem("place")
    let newObj = obj.concat(itemId)
    window.localStorage.setItem("place", newObj)
    // window.localStorage.setItem("places",place)
}

export default selectPlace
